
public class loop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int term =5 ;
	     for(int i1=1;i1<=term;i1++){ 
	     for(int j=term;j>=i1;j--){ 
	        System.out.print("* "); 
	                                    } 
	        System.out.println();
		
	}

}
}