
public class oddnumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Program to print odd numbers from 1 to 50
		
		int n = 50;
        System.out.print("Odd Numbers from 1 to " + n+ " are: ");
        for (int i = 1; i <= n; i++) {
           if (i % 2 != 0) {
                System.out.print(i + " ");
		
	}

}
}
}